import axios from 'axios';

const API_URL = 'http://localhost:5000/api/products/';

// Helper function to get the auth token, remains the same.
const getAuthHeaders = () => {
    const token = JSON.parse(localStorage.getItem('userToken'));
    if (token) {
        return { headers: { Authorization: `Bearer ${token}` } };
    }
    return {};
};

// --- CORRECTED FUNCTIONS ---
// Each function now processes the response and returns response.data

const getProducts = async () => {
    const response = await axios.get(API_URL);
    return response.data; // Return the { success, data } object
};

const getProductById = async (id) => {
    const response = await axios.get(API_URL + id);
    return response.data; // Return { success, data }
};

const createProduct = async (productData) => {
    const response = await axios.post(API_URL, productData, getAuthHeaders());
    return response.data;
};

const updateProduct = async (id, productData) => {
    const response = await axios.put(API_URL + id, productData, getAuthHeaders());
    return response.data;
};

const deleteProduct = async (id) => {
    const response = await axios.delete(API_URL + id, getAuthHeaders());
    return response.data;
};

const productService = {
    getProducts,
    getProductById,
    createProduct,
    updateProduct,
    deleteProduct,
};

export default productService;
