import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import productService from '../services/productService';

const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);

    // 1. --- CORRECTED DATA FETCHING AND INTELLIGENT DEFAULTS ---
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    const fetchedProduct = res.data;
                    setProduct(fetchedProduct);

                    // **PROFESSIONAL FIX**: Find the first IN-STOCK variant to select by default.
                    if (fetchedProduct.variants && fetchedProduct.variants.length > 0) {
                        let defaultVariant = fetchedProduct.variants.find(v => v.stock > 0);

                        // If NO variants are in stock, just take the first one to show its (Out of Stock) state.
                        if (!defaultVariant) {
                            defaultVariant = fetchedProduct.variants[0];
                        }

                        // Create the initial selectedOptions object from this default variant.
                        const initialOptions = {};
                        if (defaultVariant && fetchedProduct.optionNames) {
                            fetchedProduct.optionNames.forEach(name => {
                                const option = defaultVariant.options.find(opt => opt.name === name);
                                if (option) {
                                    initialOptions[name] = option.value;
                                }
                            });
                        }
                        setSelectedOptions(initialOptions);
                    }
                } else {
                    setError('Product not found.');
                }
            } catch (err) {
                setError('Failed to fetch product data.');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    // 2. --- THE NEW, ROBUST `useMemo` HOOK FOR DERIVED STATE ---
    // This replaces the buggy `useEffect`. It is more performant and reliable.
    const currentVariant = useMemo(() => {
        if (!product || !product.variants) return null;

        // Find the variant where ALL selected options match exactly.
        const foundVariant = product.variants.find(variant => {
            return Object.entries(selectedOptions).every(([name, value]) =>
                variant.options.find(opt => opt.name === name)?.value === value
            );
        });

        return foundVariant || null; // Return the found variant or null if no exact match
    }, [selectedOptions, product]);


    // This logic to get all possible option values remains the same and is correct.
    const availableOptions = useMemo(() => {
        if (!product || !product.variants) return {};
        const options = {};
        product.optionNames.forEach(name => {
            options[name] = [...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))];
        });
        return options;
    }, [product]);

    // 3. --- CORRECTED HANDLER FUNCTIONS ---
    const handleOptionSelect = (optionName, value) => {
        setSelectedOptions(prev => ({ ...prev, [optionName]: value }));
        setQuantity(1); // Always reset quantity when selection changes
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQuantity = prev + amount;
            if (newQuantity < 1) return 1;
            // Use the reliable `currentVariant` from `useMemo`
            if (currentVariant && newQuantity > currentVariant.stock) return currentVariant.stock;
            return newQuantity;
        });
    };

    if (loading) return <div className="text-center mt-20">Loading...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    // --- RENDER THE UI (Now with reliable data) ---
    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                <div><img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" /></div>
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    <div className="text-4xl font-extrabold text-gray-900 mb-6">
                        {currentVariant ? `â‚¹${currentVariant.price.toFixed(2)}` : 'Select options to see price'}
                    </div>

                    <div className="space-y-6">
                        {product.optionNames.map(name => (
                            <div key={name}>
                                <h3 className="text-lg font-semibold mb-2">{name}: <span className="font-normal">{selectedOptions[name]}</span></h3>
                                <div className="flex flex-wrap gap-3">
                                    {availableOptions[name].map(value => (
                                        <button key={value} onClick={() => handleOptionSelect(name, value)}
                                                className={`px-6 py-2 border rounded-md font-medium transition-all ${selectedOptions[name] === value ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500' : 'border-gray-300 hover:border-gray-500'}`}
                                        >
                                            {value}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="mt-8">
                        <p className={`text-lg font-semibold ${currentVariant && currentVariant.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {currentVariant ? `${currentVariant.stock} in stock` : 'This combination is not available'}
                        </p>
                        {currentVariant && currentVariant.stock > 0 && (
                            <div className="flex items-center mt-4">
                                <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                                <div className="flex items-center border border-gray-300 rounded-md">
                                    <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                    <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                    <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="mt-8">
                        <button disabled={!currentVariant || currentVariant.stock === 0} className="w-full py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed">
                            {currentVariant ? (currentVariant.stock > 0 ? 'Add to Cart' : 'Out of Stock') : 'Unavailable'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
