import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import productService from '../services/productService';

const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);

    // 1. --- FETCH PRODUCT DATA (UPDATED) ---
    // This effect now correctly handles both simple and variant products when setting initial state.
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    const fetchedProduct = res.data;
                    setProduct(fetchedProduct);

                    // UPDATE: Only set initial options IF it's a variant product
                    if (fetchedProduct.productType === 'variant' && fetchedProduct.variants?.length > 0) {
                        const initialOptions = {};
                        fetchedProduct.optionNames.forEach(name => {
                            // Find the option value from the first variant to serve as a default
                            initialOptions[name] = fetchedProduct.variants[0].options.find(opt => opt.name === name)?.value || '';
                        });
                        setSelectedOptions(initialOptions);
                    }
                } else {
                    setError('Product not found.');
                }
            } catch (err) {
                setError('Failed to fetch product data.');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    // 2. --- FIND CURRENT VARIANT (UPDATED & MORE EFFICIENT) ---
    // This is now derived state using useMemo for better performance.
    // It will only re-calculate when the selected options or the product itself changes.
    const currentVariant = useMemo(() => {
        // UPDATE: If it's not a variant product, there's no current variant.
        if (!product || product.productType !== 'variant') return null;

        // This logic is more robust. It ensures a variant matches ALL selected options.
        return product.variants.find(variant =>
            product.optionNames.every(name =>
                variant.options.find(opt => opt.name === name)?.value === selectedOptions[name]
            )
        );
    }, [selectedOptions, product]);

    // 3. --- AVAILABLE OPTIONS (UPDATED) ---
    // This logic is mostly the same, but benefits from being memoized.
    const availableOptions = useMemo(() => {
        if (!product || product.productType !== 'variant') return {};
        const options = {};
        product.optionNames.forEach(name => {
            options[name] = [...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))];
        });
        return options;
    }, [product]);

    // 4. --- HANDLER FUNCTIONS (UPDATED) ---
    const handleOptionSelect = (optionName, value) => {
        setSelectedOptions(prev => ({ ...prev, [optionName]: value }));
        setQuantity(1); // Reset quantity whenever a new variant is chosen
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQuantity = prev + amount;
            if (newQuantity < 1) return 1;
            // UPDATE: Check the correct stock based on product type
            const maxStock = product.productType === 'simple' ? product.stock : (currentVariant ? currentVariant.stock : 0);
            if (newQuantity > maxStock) return maxStock;
            return newQuantity;
        });
    };

    // --- RENDER LOGIC ---
    if (loading) return <div className="text-center mt-20">Loading Product...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    // 5. --- DERIVE FINAL STATE FOR UI (NEW) ---
    // This makes the JSX cleaner and easier to read.
    const stock = product.productType === 'simple' ? product.stock : (currentVariant ? currentVariant.stock : 0);
    const displayPrice = product.productType === 'simple' ? product.price : (currentVariant ? currentVariant.price : 0);
    const canAddToCart = product.productType === 'simple' ? stock > 0 : (currentVariant && stock > 0);
    const isVariantAvailable = product.productType === 'variant' && currentVariant;

    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                {/* --- IMAGE (Works for both) --- */}
                <div>
                    <img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" />
                </div>

                {/* --- DETAILS (Conditionally renders based on productType) --- */}
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    {/* --- PRICE DISPLAY --- */}
                    <div className="text-4xl font-extrabold text-gray-900 mb-6">
                        {/* UPDATE: Intelligent price display */}
                        {product.productType === 'simple' || isVariantAvailable ? `â‚¹${displayPrice.toFixed(2)}` : 'Select options to see price'}
                    </div>

                    {/* --- VARIANT OPTIONS (Only shows for variant products) --- */}
                    {product.productType === 'variant' && (
                        <div className="space-y-6">
                            {product.optionNames.map(name => (
                                <div key={name}>
                                    <h3 className="text-lg font-semibold mb-2">{name}: <span className="font-normal">{selectedOptions[name]}</span></h3>
                                    <div className="flex flex-wrap gap-3">
                                        {availableOptions[name].map(value => (
                                            <button key={value} onClick={() => handleOptionSelect(name, value)} className={`px-6 py-2 border rounded-md font-medium transition-all ${selectedOptions[name] === value ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500' : 'border-gray-300 hover:border-gray-500'}`}>
                                                {value}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* --- STOCK & QUANTITY (Works for both) --- */}
                    <div className="mt-8">
                        <p className={`text-lg font-semibold ${stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {/* UPDATE: Intelligent stock message */}
                            {product.productType === 'variant' && !isVariantAvailable ? 'This combination is not available' : `${stock} in stock`}
                        </p>
                        {stock > 0 && (
                            <div className="flex items-center mt-4">
                                <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                                <div className="flex items-center border border-gray-300 rounded-md">
                                    <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                    <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                    <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* --- ADD TO CART (Works for both) --- */}
                    <div className="mt-8">
                        <button disabled={!canAddToCart} className="w-full py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed">
                            {canAddToCart ? 'Add to Cart' : (product.productType === 'variant' ? 'Unavailable' : 'Out of Stock')}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
