import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Header = () => {
    const { isAuthenticated, isAdmin, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    // Base styles for navigation links
    const navLinkStyle = "text-gray-300 hover:text-white transition-colors duration-300";

    // Base styles for all action buttons for a consistent look
    const buttonBaseStyle = "px-4 py-2 rounded-md text-sm font-semibold transition-all duration-300 shadow-sm";

    // Specific styles for different button types
    const primaryButtonStyle = `${buttonBaseStyle} bg-indigo-600 text-white hover:bg-indigo-500`;
    const secondaryButtonStyle = `${buttonBaseStyle} bg-gray-700 text-gray-200 hover:bg-gray-600`;
    const adminButtonStyle = `${buttonBaseStyle} bg-yellow-500 text-black hover:bg-yellow-400`;


    return (
        <header className="bg-gray-900 text-white shadow-lg w-full sticky top-0 z-50">
            <div className="container mx-auto flex items-center justify-between p-4">

                {/* Logo */}
                <div className="text-2xl font-bold">
                    <Link to="/">ProSports</Link>
                </div>

                {/* Navigation Links and Buttons */}
                <div className="flex items-center space-x-4">
                    <nav className="hidden md:flex items-center space-x-4">
                        <Link to="/" className={navLinkStyle}>Home</Link>
                        {isAuthenticated && <Link to="/profile" className={navLinkStyle}>Profile</Link>}
                    </nav>

                    {/* Separator */}
                    <div className="hidden md:block w-px h-6 bg-gray-600"></div>

                    {/* Action Buttons */}
                    <div className="flex items-center space-x-3">
                        {isAuthenticated ? (
                            <>
                                {isAdmin && (
                                    <Link to="/admin" className={adminButtonStyle}>
                                        Admin Panel
                                    </Link>
                                )}
                                <button onClick={handleLogout} className={secondaryButtonStyle}>
                                    Logout
                                </button>
                            </>
                        ) : (
                            <>
                                <Link to="/login" className={secondaryButtonStyle}>Login</Link>
                                <Link to="/register" className={primaryButtonStyle}>Register</Link>
                            </>
                        )}
                        <Link to="/cart" className={primaryButtonStyle}>
                            Cart
                        </Link>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Header;
