import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Header = () => {
    const { isAuthenticated, isAdmin, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <header className="bg-gray-800 text-white shadow-lg w-full">
            {/* The container handles the centered max-width content */}
            <div className="container mx-auto flex items-center justify-between p-4">
                {/* Logo */}
                <div className="text-2xl font-bold">
                    <Link to="/">ProSports</Link>
                </div>

                {/* Navigation Links */}
                <nav className="flex items-center space-x-4 md:space-x-6 text-sm md:text-base">
                    <Link to="/" className="hover:text-indigo-400 transition-colors duration-300">Home</Link>

                    {isAuthenticated ? (
                        <>
                            {isAdmin && (
                                <Link to="/admin" className="px-3 py-1 bg-yellow-500 text-black rounded hover:bg-yellow-400 font-semibold">
                                    Admin
                                </Link>
                            )}
                            {/* We will create the profile page later */}
                            <Link to="/profile" className="hover:text-indigo-400 transition-colors duration-300">Profile</Link>
                            <button onClick={handleLogout} className="hover:text-indigo-400 transition-colors duration-300">
                                Logout
                            </button>
                        </>
                    ) : (
                        <>
                            <Link to="/login" className="hover:text-indigo-400 transition-colors duration-300">Login</Link>
                            <Link to="/register" className="hover:text-indigo-400 transition-colors duration-300">Register</Link>
                        </>
                    )}

                    {/* We will create the cart page later */}
                    <Link to="/cart" className="p-2 bg-indigo-600 rounded-full hover:bg-indigo-500 transition-colors duration-300">
                        {/* You can add a cart icon here later */}
                        Cart
                    </Link>
                </nav>
            </div>
        </header>
    );
};

export default Header;
