import React, { useState, useEffect } from 'react';

const ProductForm = ({ onSubmit, initialData = {}, loading }) => {
    const [product, setProduct] = useState({
        name: '',
        description: '',
        price: '',
        category: '',
        stock: '',
        imageUrl: '',
    });

    useEffect(() => {
        if (initialData) {
            setProduct({
                name: initialData.name || '',
                description: initialData.description || '',
                price: initialData.price || '',
                category: initialData.category || '',
                stock: initialData.stock || '',
                imageUrl: initialData.imageUrl || '',
            });
        }
    }, [initialData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(product);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <InputField name="name" label="Product Name" value={product.name} onChange={handleChange} />
            <InputField name="description" label="Description" type="textarea" value={product.description} onChange={handleChange} />
            <InputField name="price" label="Price" type="number" value={product.price} onChange={handleChange} />
            <InputField name="category" label="Category" value={product.category} onChange={handleChange} />
            <InputField name="stock" label="Stock" type="number" value={product.stock} onChange={handleChange} />
            <InputField name="imageUrl" label="Image URL" value={product.imageUrl} onChange={handleChange} />

            <button type="submit" disabled={loading} className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300">
                {loading ? 'Saving...' : 'Save Product'}
            </button>
        </form>
    );
};

// Helper component for consistent input fields
const InputField = ({ name, label, value, onChange, type = 'text' }) => (
    <div>
        <label htmlFor={name} className="block text-sm font-medium text-gray-700">{label}</label>
        {type === 'textarea' ? (
            <textarea id={name} name={name} value={value} onChange={onChange} rows="3" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"></textarea>
        ) : (
            <input id={name} name={name} type={type} value={value} onChange={onChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" />
        )}
    </div>
);

export default ProductForm;
