import React from 'react';
import { Outlet } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AdminLoginPage from '../pages/admin/AdminLoginPage';

const AdminLayout = () => {
    const { isAuthenticated, isAdmin, loading } = useAuth();

    // Show a loading screen while we verify the user's token
    if (loading) {
        return <div className="text-center mt-20">Loading...</div>;
    }

    // If the user is authenticated AND is an admin, show the admin content
    if (isAuthenticated && isAdmin) {
        return <Outlet />; // This will render the nested route (e.g., dashboard, product list)
    }

    // In all other cases (not logged in, or not an admin), show the AdminLoginPage
    return <AdminLoginPage />;
};

export default AdminLayout;
