import axios from 'axios';

const API_URL = '/api/categories/';

const getAuthHeaders = () => { /* ... same getAuthHeaders helper as in productService ... */ };

const createCategory = (categoryData) => axios.post(API_URL, categoryData, getAuthHeaders());
const updateCategory = (id, categoryData) => axios.put(API_URL + id, categoryData, getAuthHeaders());
const deleteCategory = (id) => axios.delete(API_URL + id, getAuthHeaders());

const categoryService = {
    createCategory,
    updateCategory,
    deleteCategory,
};

export default categoryService;
