import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ProductCard from '../components/products/ProductCard'; // We will reuse the card

const CategoryPage = () => {
    const { slug } = useParams();
    const [products, setProducts] = useState([]);
    const [categoryName, setCategoryName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProductsByCategory = async () => {
            setLoading(true);
            try {
                const res = await axios.get(`http://localhost:5000/api/products/category/${slug}`);
                if (res.data.success) {
                    setProducts(res.data.data);
                    setCategoryName(res.data.categoryName);
                }
            } catch (err) {
                setError('Could not load products for this category.');
            } finally {
                setLoading(false);
            }
        };
        fetchProductsByCategory();
    }, [slug]);

    if (loading) return <div className="text-center mt-20">Loading Products...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;

    return (
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12">
            <h1 className="text-4xl font-extrabold text-gray-900 mb-12">
                {categoryName}1111111111111111
            </h1>
            {products.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                    {products.map((product) => (
                        <ProductCard key={product._id} product={product} />
                    ))}
                </div>
            ) : (
                <p>No products found in this category.</p>
            )}
        </div>
    );
};

export default CategoryPage;
