import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

const AdminLoginPage = () => {
    const [formData, setFormData] = useState({ identifier: '', password: '' });
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const { login } = useAuth();

    const onChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null); // Clear previous errors on a new attempt
        try {
            await login(formData);
            // On success, the parent component will handle showing the dashboard
        } catch (err) {
            // Set a user-friendly error message from the server or a default one
            setError(err.response?.data?.msg || 'Invalid credentials. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-xl shadow-2xl">
                <div className="text-center">
                    <h2 className="text-3xl font-extrabold text-gray-900">Admin Portal</h2>
                    <p className="mt-2 text-sm text-gray-600">Please sign in to continue</p>
                </div>
                <form className="space-y-6" onSubmit={onSubmit}>
                    <div className="space-y-4">
                        <input
                            name="identifier"
                            type="text"
                            required
                            value={formData.identifier}
                            onChange={onChange}
                            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-600"
                            placeholder="Email or Mobile Number"
                        />
                        <input
                            name="password"
                            type="password"
                            required
                            value={formData.password}
                            onChange={onChange}
                            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-600"
                            placeholder="Password"
                        />
                    </div>

                    {/* --- THIS IS THE IMPROVED ERROR MESSAGE DISPLAY --- */}
                    {/* It's now a styled alert box with an icon, making it very clear. */}
                    {error && (
                        <div className="flex items-center justify-center text-sm text-center text-red-700 bg-red-100 p-3 rounded-md">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 flex-shrink-0" viewBox="0 0 20 20" fill="currentColor">
                                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                            </svg>
                            <span>{error}</span>
                        </div>
                    )}

                    <div>
                        <button type="submit" disabled={loading} className="w-full py-3 px-4 font-semibold text-white bg-purple-600 rounded-md hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:bg-purple-300">
                            {loading ? 'Signing In...' : 'Sign In'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default AdminLoginPage;
