import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CategoryNavigation = () => {
    const [categories, setCategories] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [activeCategory, setActiveCategory] = useState(null);
    const menuRef = useRef(null); // Ref to handle clicks outside the menu

    // --- Fetch Categories (remains the same) ---
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/categories');
                if (res.data.success) {
                    setCategories(res.data.data);
                }
            } catch (error) {
                console.error("Failed to fetch categories", error);
            }
        };
        fetchCategories();
    }, []);

    // --- Click Outside Handler (Professional UX Improvement) ---
    // This will close the menu if you click anywhere else on the page.
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [menuRef]);

    const handleMouseEnter = (category) => {
        setActiveCategory(category);
    };

    // --- RENDER ---
    return (
        // We attach the ref to the main container
        <div className="bg-white shadow-md relative z-40" ref={menuRef}>
            <div className="container mx-auto flex items-center p-3">
                <button
                    onClick={() => setIsOpen(!isOpen)}
                    className="flex items-center px-4 py-2 border rounded-md font-semibold text-gray-700 hover:bg-gray-100 transition-colors"
                >
                    <svg className="w-6 h-6 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path></svg>
                    Categories
                    <svg className={`w-5 h-5 ml-2 transition-transform duration-200 ${isOpen ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path></svg>
                </button>
            </div>

            {isOpen && (
                <div className="absolute top-full left-0 w-full container mx-auto flex shadow-lg border-t bg-white">
                    {/* Panel 1: Main Categories */}
                    <div className="w-1/4 border-r">
                        {categories.map(cat => (
                            <Link
                                to={`/category/${cat.slug}`}
                                key={cat._id}
                                className={`flex items-center px-4 py-3 text-gray-700 hover:bg-gray-100 ${activeCategory?._id === cat._id ? 'bg-gray-100' : ''}`}
                                onMouseEnter={() => handleMouseEnter(cat)}
                                onClick={() => setIsOpen(false)} // Close menu on click
                            >
                                {cat.name}
                                {/* Show arrow only if there are children */}
                                {cat.children && cat.children.length > 0 && <span className="ml-auto">></span>}
                            </Link>
                        ))}
                        <Link to="/categories" onClick={() => setIsOpen(false)} className="flex items-center px-4 py-3 text-blue-600 font-semibold hover:bg-gray-100">
                            View All Categories >
                        </Link>
                    </div>

                    {/* Panel 2: Sub-Categories */}
                    <div className="w-3/4 p-4">
                        {/*
                            --- THE DEFINITIVE FIX ---
                            This checks for activeCategory FIRST, and then checks if its children array
                            is valid and has items before attempting to map over it.
                        */}
                        {activeCategory && activeCategory.children && activeCategory.children.length > 0 ? (
                            <div>
                                <h3 className="font-bold text-lg mb-2">{activeCategory.name}</h3>
                                <div className="grid grid-cols-3 gap-4">
                                    {activeCategory.children.map(subCat => (
                                        <Link
                                            to={`/category/${subCat.slug}`}
                                            key={subCat._id}
                                            className="block py-1 text-gray-600 hover:text-blue-600"
                                            onClick={() => setIsOpen(false)} // Close menu on click
                                        >
                                            {subCat.name}
                                        </Link>
                                    ))}
                                </div>
                            </div>
                        ) : (
                            <div className="flex items-center justify-center h-full text-gray-500">
                                {activeCategory ? `Shop all in ${activeCategory.name}` : 'Hover over a category to see options'}
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default CategoryNavigation;
