import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
    return (
        // The card itself
        <div className="bg-white rounded-lg shadow-md overflow-hidden transform hover:-translate-y-1 transition-transform duration-300">
            <Link to={`/product/${product._id}`}>
                <img src={product.imageUrl} alt={product.name} className="w-full h-56 object-cover" />
            </Link>
            <div className="p-4">
                <h2 className="text-xl font-semibold text-gray-800 truncate">{product.name}</h2>
                <p className="text-gray-600 mt-1 h-10 overflow-hidden">{product.description}</p>
                <div className="flex items-center justify-between mt-4">
                    <span className="text-2xl font-bold text-gray-900">${product.price.toFixed(2)}</span>
                    <button className="px-4 py-2 bg-indigo-600 text-white text-sm font-semibold rounded-lg hover:bg-indigo-500 transition-colors duration-300">
                        Add to Cart
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ProductCard;
