import React, { useState, useEffect, useMemo } from 'react';
import { useParams, Link } from 'react-router-dom';
import productService from '../services/productService';

const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- STATE FOR USER SELECTIONS ---
    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);

    // --- 1. FETCH PRODUCT DATA AND INITIALIZE STATE (CRITICAL FIX) ---
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            setError('');
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    const fetchedProduct = res.data;
                    setProduct(fetchedProduct);

                    // **THIS IS THE KEY FIX:**
                    // If it's a variant product, set the default selected options
                    // to the first available variant. This makes the page load in a valid state.
                    if (fetchedProduct.productType === 'variant' && fetchedProduct.variants?.length > 0) {
                        const initialOptions = {};
                        fetchedProduct.variants[0].options.forEach(opt => {
                            initialOptions[opt.name] = opt.value;
                        });
                        setSelectedOptions(initialOptions);
                    }
                } else {
                    setError('Product not found.');
                }
            } catch (err) {
                setError('Failed to fetch product data.');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    // --- 2. FIND THE CURRENT VARIANT BASED ON SELECTIONS ---
    // useMemo is more efficient here than useEffect.
    const currentVariant = useMemo(() => {
        if (product?.productType !== 'variant' || !product.variants) return null;

        return product.variants.find(variant =>
            variant.options.every(opt => selectedOptions[opt.name] === opt.value)
        );
    }, [selectedOptions, product]);

    const handleOptionSelect = (optionName, value) => {
        setSelectedOptions(prev => ({ ...prev, [optionName]: value }));
        setQuantity(1); // Reset quantity when selection changes
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQuantity = prev + amount;
            if (newQuantity < 1) return 1;
            const maxStock = currentVariant ? currentVariant.stock : product.stock;
            if (newQuantity > maxStock) return maxStock;
            return newQuantity;
        });
    };

    if (loading) return <div className="text-center mt-20 text-xl">Loading Product...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    // --- 3. RENDER THE CORRECT UI BASED ON PRODUCT TYPE ---
    const renderProductDetails = () => {
        if (product.productType === 'simple') {
            return (
                <div>
                    <div className="text-4xl font-extrabold text-gray-900 mb-6">₹{product.price.toFixed(2)}</div>
                    <p className={`text-lg font-semibold ${product.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {product.stock} in stock
                    </p>
                    {/* Quantity for Simple Product */}
                    {product.stock > 0 && (
                        <div className="flex items-center mt-4">
                            <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                            <div className="flex items-center border border-gray-300 rounded-md">
                                <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                            </div>
                        </div>
                    )}
                </div>
            );
        }

        if (product.productType === 'variant') {
            return (
                <>
                    <div className="text-4xl font-extrabold text-gray-900 mb-6">
                        {currentVariant ? `₹${currentVariant.price.toFixed(2)}` : 'Unavailable'}
                    </div>

                    {/* Intelligent Option Selectors */}
                    <div className="space-y-6">
                        {product.optionNames.map(name => (
                            <div key={name}>
                                <h3 className="text-lg font-semibold mb-2">{name}: <span className="font-normal">{selectedOptions[name]}</span></h3>
                                <div className="flex flex-wrap gap-3">
                                    {/* Get all possible values for this option */}
                                    {[...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))].map(value => (
                                        <button
                                            key={value}
                                            onClick={() => handleOptionSelect(name, value)}
                                            className={`px-6 py-2 border rounded-md text-center font-medium transition-all ${selectedOptions[name] === value ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500' : 'border-gray-300 hover:border-gray-500'}`}
                                        >
                                            {value}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Stock and Quantity for Variant Product */}
                    <div className="mt-8">
                        <p className={`text-lg font-semibold ${currentVariant && currentVariant.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {currentVariant ? `${currentVariant.stock} in stock` : 'This combination is not available'}
                        </p>
                        {currentVariant && currentVariant.stock > 0 && (
                            <div className="flex items-center mt-4">
                                <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                                <div className="flex items-center border border-gray-300 rounded-md">
                                    <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                    <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                    <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                                </div>
                            </div>
                        )}
                    </div>
                </>
            );
        }
        return null;
    };

    // Determine Add to Cart button state
    const isAddToCartDisabled = product.productType === 'simple' ? product.stock === 0 : !currentVariant || currentVariant.stock === 0;
    const addToCartText = product.productType === 'simple' ? (product.stock > 0 ? 'Add to Cart' : 'Out of Stock') : (currentVariant ? (currentVariant.stock > 0 ? 'Add to Cart' : 'Out of Stock') : 'Unavailable');

    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                {/* Left Side: Image */}
                <div>
                    <img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" />
                </div>

                {/* Right Side: Details and Options */}
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    {renderProductDetails()}

                    {/* Universal Add to Cart Button */}
                    <div className="mt-8">
                        <button
                            disabled={isAddToCartDisabled}
                            className="w-full py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
                        >
                            {addToCartText}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
