import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import authService from '../services/authService';
import { useJsApiLoader } from '@react-google-maps/api';

// Eye icon for password visibility toggle (no changes needed)
const EyeIcon = ({ onClick, isVisible }) => (
    <svg onClick={onClick} xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400 cursor-pointer" viewBox="0 0 20 20" fill="currentColor">
        {isVisible ? <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" /> : <path fillRule="evenodd" d="M10 3a7 7 0 00-7 7c0 1.545.446 2.973 1.215 4.195.16.2.33.394.508.58.178.186.364.364.552.53C6.46 16.49 8.12 17 10 17s3.54-.51 4.725-1.695c.188-.166.374-.344.552-.53.178-.186.348-.38.508-.58A6.97 6.97 0 0017 10a7 7 0 00-7-7zm-5.707 7.293a5.002 5.002 0 018.829-1.393A4.997 4.997 0 0110 15a4.997 4.997 0 01-4.33-2.5A4.985 4.985 0 014.293 10.293z" clipRule="evenodd" />}
    </svg>
);


const RegisterPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '', lastName: '', email: '', phone: '',
        password: '', confirmPassword: '',
        address: '', city: '', state: '', zipcode: '',
    });
    const [agreed, setAgreed] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    // Create a ref for the address input element
    const addressInputRef = useRef(null);

    // Load the Google Maps API script
    const { isLoaded, loadError } = useJsApiLoader({
        googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
        libraries: ['places'],
    });

    // This useEffect hook attaches the Google Autocomplete to our input field
    // It only runs when the API script has successfully loaded.
    useEffect(() => {
        if (isLoaded && addressInputRef.current) {
            const autocomplete = new window.google.maps.places.Autocomplete(
                addressInputRef.current,
                {
                    fields: ["address_components", "formatted_address"],
                    types: ["address"],
                }
            );

            autocomplete.addListener("place_changed", () => {
                const place = autocomplete.getPlace();
                if (place && place.address_components) {
                    let city = '', state = '', zipcode = '';
                    const address = place.formatted_address || '';

                    for (const component of place.address_components) {
                        const types = component.types;
                        if (types.includes('locality')) city = component.long_name;
                        if (types.includes('administrative_area_level_1')) state = component.long_name;
                        if (types.includes('postal_code')) zipcode = component.long_name;
                    }

                    // Update state with the selected place data
                    setFormData(prev => ({ ...prev, address, city, state, zipcode }));
                }
            });
        }
    }, [isLoaded]); // The dependency array ensures this runs only when `isLoaded` changes

    // This is the standard React onChange handler. It will ALWAYS work.
    const onChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const onSubmit = async e => {
        e.preventDefault();
        setError('');
        if (formData.password !== formData.confirmPassword) return setError('Passwords do not match.');
        if (formData.password.length < 8) return setError('Password must be at least 8 characters long.');
        if (!agreed) return setError('You must agree to the terms and conditions.');

        setLoading(true);
        try {
            await authService.register(formData);
            alert('Registration successful! Please sign in.');
            navigate('/login');
        } catch (err) {
            setError(err.msg || 'An error occurred during registration.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-4xl w-full space-y-8 p-10 bg-white rounded-xl shadow-lg">
                <div>
                    <h2 className="mt-6 text-3xl font-extrabold text-gray-900" style={{ color: '#0d6efd' }}>Sign up</h2>
                    <p className="mt-2 text-sm text-gray-600">No Account? Register control your order.</p>
                </div>
                {loadError && <p className="text-sm text-red-600 text-center">Could not load address suggestions. Please enter your address manually.</p>}
                <form className="mt-8 space-y-6" onSubmit={onSubmit}>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-x-6 gap-y-4">
                        <InputField label="First name" name="firstName" value={formData.firstName} onChange={onChange} placeholder="first name" />
                        <InputField label="Last name" name="lastName" value={formData.lastName} onChange={onChange} placeholder="last name" />
                        <InputField label="Email address" name="email" type="email" value={formData.email} onChange={onChange} placeholder="email address" />
                        <InputField label="Phone number" name="phone" value={formData.phone} onChange={onChange} placeholder="mobile number" />
                        <PasswordField label="Password" name="password" value={formData.password} onChange={onChange} show={showPassword} toggle={() => setShowPassword(!showPassword)} />
                        <PasswordField label="Confirm password" name="confirmPassword" value={formData.confirmPassword} onChange={onChange} show={showConfirmPassword} toggle={() => setShowConfirmPassword(!showConfirmPassword)} />

                        {/* Address and related fields */}
                        <div className="md:col-span-2">
                            <label htmlFor="address" className="text-sm font-medium" style={{ color: '#0d6efd' }}>Address</label>
                            {/* This is a standard, fully controlled React input. */}
                            <input
                                ref={addressInputRef}
                                id="address"
                                name="address"
                                type="text"
                                required
                                value={formData.address}
                                onChange={onChange}
                                placeholder="Start typing your address..."
                                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            />
                        </div>

                        {/* These fields are ALWAYS editable */}
                        <InputField label="City" name="city" value={formData.city} onChange={onChange} placeholder="City" />
                        <InputField label="State" name="state" value={formData.state} onChange={onChange} placeholder="State" />
                        <InputField label="Zipcode" name="zipcode" value={formData.zipcode} onChange={onChange} placeholder="Zipcode" />
                    </div>

                    <div className="flex items-center">
                        <input id="terms" name="terms" type="checkbox" checked={agreed} onChange={(e) => setAgreed(e.target.checked)} className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded" />
                        <label htmlFor="terms" className="ml-2 block text-sm text-gray-900">I agree to Your <a href="#" className="font-medium" style={{ color: '#0d6efd' }}>Terms and conditions</a></label>
                    </div>

                    {error && <p className="text-sm text-red-600 text-center">{error}</p>}

                    <div>
                        <button type="submit" disabled={loading} className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400">
                            {loading ? 'Registering...' : 'Sign up'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

// Helper components for inputs (no changes)
const InputField = ({ label, name, type = 'text', value, onChange, placeholder }) => (
    <div>
        <label htmlFor={name} className="text-sm font-medium" style={{ color: '#0d6efd' }}>{label}</label>
