import axios from 'axios';

const API_URL = 'http://localhost:5000/api/auth/';

/**
 * Registers a new user
 * @param {object} userData - { name, email, password }
 */
const register = async (userData) => {
    try {
        const response = await axios.post(API_URL + 'register', userData);
        // On successful registration, the backend sends back a token.
        // We can store it in localStorage if we want to log the user in immediately.
        if (response.data.token) {
            localStorage.setItem('userToken', JSON.stringify(response.data.token));
        }
        return response.data;
    } catch (error) {
        // Axios wraps the error response in error.response
        throw error.response.data;
    }
};

// We will add the login function here later

const authService = {
    register,
};

export default authService;
