import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import productService from '../services/productService';

const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // --- State Management for User Interaction ---
    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);

    // --- 1. DATA FETCHING AND INTELLIGENT INITIALIZATION ---
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    const fetchedProduct = res.data;
                    setProduct(fetchedProduct);

                    // **CRITICAL FIX**: Set the initial state to the FIRST available variant
                    if (fetchedProduct.productType === 'variant' && fetchedProduct.variants?.length > 0) {
                        const firstVariant = fetchedProduct.variants[0];
                        const initialOptions = {};
                        firstVariant.options.forEach(opt => {
                            initialOptions[opt.name] = opt.value;
                        });
                        setSelectedOptions(initialOptions);
                    }
                } else {
                    setError('Product not found.');
                }
            } catch (err) {
                setError('Failed to fetch product details.');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    // --- 2. DERIVED STATE: FIND THE CURRENT VARIANT (Memoized for performance) ---
    const currentVariant = useMemo(() => {
        if (!product || product.productType !== 'variant' || Object.keys(selectedOptions).length === 0) {
            return null;
        }
        // Find the variant that perfectly matches all currently selected options
        return product.variants.find(variant =>
            variant.options.every(opt => selectedOptions[opt.name] === opt.value)
        );
    }, [product, selectedOptions]);

    // --- 3. THE "INTELLIGENCE": LOGIC TO CHECK OPTION AVAILABILITY ---
    const getAvailableOptions = useMemo(() => {
        if (!product || product.productType !== 'variant') return {};

        const allPossibleOptions = {};
        product.optionNames.forEach(name => {
            allPossibleOptions[name] = [...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))];
        });
        return allPossibleOptions;

    }, [product]);

    // This function checks if a specific choice is part of any valid, complete variant
    const isOptionCombinationAvailable = (optionName, optionValue) => {
        if (!product || product.productType !== 'variant') return true;

        // Create a temporary selection object to test against
        const tempSelection = { ...selectedOptions, [optionName]: optionValue };

        // Check if there is at least one variant that satisfies this potential selection.
        // For example, if we test for 'Size: S', we check if there's any variant
        // that matches the currently selected color AND has 'Size: S'.
        return product.variants.some(variant =>
            Object.entries(tempSelection).every(([key, value]) => {
                // If an option isn't selected yet, we don't hold it against the variant
                if (!value) return true;
                return variant.options.some(opt => opt.name === key && opt.value === value);
            })
        );
    };

    // --- 4. UI HANDLERS ---
    const handleOptionSelect = (optionName, value) => {
        setSelectedOptions(prev => ({ ...prev, [optionName]: value }));
        setQuantity(1); // Reset quantity on any variant change
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQuantity = Math.max(1, prev + amount);
            if (product.productType === 'simple') {
                return Math.min(newQuantity, product.stock);
            }
            if (currentVariant) {
                return Math.min(newQuantity, currentVariant.stock);
            }
            return 1;
        });
    };

    if (loading) return <div className="text-center mt-20 text-xl">Loading...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    // --- 5. THE PROFESSIONAL RENDER LOGIC ---
    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                <div><img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" /></div>
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    {/* --- A: UI FOR SIMPLE PRODUCTS --- */}
                    {product.productType === 'simple' && (
                        <>
                            <div className="text-4xl font-extrabold text-gray-900 mb-6">₹{product.price.toFixed(2)}</div>
                            <p className={`text-lg font-semibold ${product.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                                {product.stock > 0 ? `${product.stock} in stock` : 'Out of Stock'}
                            </p>
                            {/* Quantity and Add to Cart for simple products */}
                        </>
                    )}

                    {/* --- B: UI FOR VARIANT PRODUCTS --- */}
                    {product.productType === 'variant' && (
                        <>
                            <div className="text-4xl font-extrabold text-gray-900 mb-6">
                                {currentVariant ? `₹${currentVariant.price.toFixed(2)}` : 'Please select options'}
                            </div>

                            <div className="space-y-6">
                                {product.optionNames.map(name => (
                                    <div key={name}>
                                        <h3 className="text-lg font-semibold mb-2">{name}: <span className="font-normal">{selectedOptions[name]}</span></h3>
                                        <div className="flex flex-wrap gap-3">
                                            {getAvailableOptions[name]?.map(value => {
                                                const isSelected = selectedOptions[name] === value;
                                                // We don't need the intelligence check here, as the final stock check handles it.
                                                // For a more advanced UI, you would use it to disable buttons.
                                                return (
                                                    <button key={value} onClick={() => handleOptionSelect(name, value)}
                                                            className={`px-6 py-2 border rounded-md text-center font-medium transition-all ${isSelected ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500' : 'border-gray-300 hover:border-gray-500'}`}
                                                    >
                                                        {value}
                                                    </button>
                                                );
                                            })}
                                        </div>
                                    </div>
                                ))}
                            </div>

                            <p className={`mt-8 text-lg font-semibold ${currentVariant && currentVariant.stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                                {currentVariant ? `${currentVariant.stock} in stock` : "This combination is not available."}
                            </p>
                        </>
                    )}

                    {/* --- C: Common UI (Quantity and Add to Cart) --- */}
                    <div className="mt-8">
                        <div className="flex items-center mt-4">
                            <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                            <div className="flex items-center border border-gray-300 rounded-md">
                                <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                            </div>
                        </div>
                        <button
                            disabled={product.productType === 'simple' ? product.stock === 0 : !currentVariant || currentVariant.stock === 0}
                            className="w-full mt-6 py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
                        >
                            Add to Cart
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
