import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import authService from '../services/authService';

// --- Helper Components & Data ---

// Eye icon for password visibility
const EyeIcon = ({ visible }) => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        {visible ? (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
        ) : (
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-1.293-1.293a3 3 0 014.242-4.243l-1.292 1.292" />
        )}
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
    </svg>
);


// Sample data for State -> City mapping
const locationData = {
    "California": ["Los Angeles", "San Francisco", "San Diego"],
    "New York": ["New York City", "Buffalo", "Rochester"],
    "Texas": ["Houston", "Austin", "Dallas"],
};
const states = Object.keys(locationData);

// --- Main Register Page Component ---

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        firstName: '', lastName: '', email: '', phone: '',
        password: '', confirmPassword: '',
        address: '', state: '', city: '', zipcode: '',
    });
    const [agreeTerms, setAgreeTerms] = useState(false);
    const [cities, setCities] = useState([]);
    const [passwordVisible, setPasswordVisible] = useState(false);
    const [confirmPasswordVisible, setConfirmPasswordVisible] = useState(false);

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    // Effect to update cities when state changes
    useEffect(() => {
        if (formData.state) {
            setCities(locationData[formData.state]);
            setFormData(prev => ({ ...prev, city: '' })); // Reset city on state change
        } else {
            setCities([]);
        }
    }, [formData.state]);

    const onChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const onSubmit = async (e) => {
        e.preventDefault();
        setError(null);

        // --- Validation ---
        if (formData.password !== formData.confirmPassword) {
            return setError("Passwords do not match.");
        }
        if (formData.password.length < 8) {
            return setError("Password must be at least 8 characters long.");
        }
        if (!agreeTerms) {
            return setError("You must agree to the terms and conditions.");
        }

        setLoading(true);
        try {
            // We don't need to send confirmPassword to the backend
            const { confirmPassword, ...userData } = formData;
            await authService.register(userData);

            alert('Registration successful! Please proceed to the login page.');
            navigate('/login');
        } catch (err) {
            setError(err.msg || 'Registration failed. Please check your details and try again.');
        } finally {
            setLoading(false);
        }
    };

    // A reusable input field component for consistency
    const InputField = ({ name, label, type = "text", placeholder, value, onChange }) => (
        <div>
            <label htmlFor={name} className="block text-sm font-medium text-blue-700">{label}</label>
            <input type={type} name={name} id={name} placeholder={placeholder} value={value} onChange={onChange} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
        </div>
    );

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
            <div className="sm:mx-auto sm:w-full sm:max-w-4xl">
                <h2 className="text-3xl font-extrabold text-blue-900">Sign up</h2>
                <p className="mt-2 text-sm text-gray-600">No Account? Register control your order.</p>
            </div>

            <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-4xl">
                <div className="bg-white py-8 px-4 shadow-lg sm:rounded-lg sm:px-10">
                    <form className="space-y-6" onSubmit={onSubmit}>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-x-6 gap-y-6">
                            <InputField name="firstName" label="First name" placeholder="first name" value={formData.firstName} onChange={onChange} />
                            <InputField name="lastName" label="Last name" placeholder="last name" value={formData.lastName} onChange={onChange} />
                            <InputField name="email" label="Email address" type="email" placeholder="email address" value={formData.email} onChange={onChange} />
                            <InputField name="phone" label="Phone number" type="tel" placeholder="mobile number" value={formData.phone} onChange={onChange} />

                            {/* Password Field with visibility toggle */}
                            <div>
                                <label className="block text-sm font-medium text-blue-700">Password</label>
                                <div className="mt-1 relative">
                                    <input type={passwordVisible ? "text" : "password"} name="password" value={formData.password} onChange={onChange} required placeholder="Minimum 8 characters long" className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                                    <button type="button" onClick={() => setPasswordVisible(!passwordVisible)} className="absolute inset-y-0 right-0 px-3 flex items-center">
                                        <EyeIcon visible={passwordVisible} />
                                    </button>
                                </div>
                            </div>

                            {/* Confirm Password Field with visibility toggle */}
                            <div>
                                <label className="block text-sm font-medium text-blue-700">Confirm password</label>
                                <div className="mt-1 relative">
                                    <input type={confirmPasswordVisible ? "text" : "password"} name="confirmPassword" value={formData.confirmPassword} onChange={onChange} required placeholder="Minimum 8 characters long" className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500" />
                                    <button type="button" onClick={() => setConfirmPasswordVisible(!confirmPasswordVisible)} className="absolute inset-y-0 right-0 px-3 flex items-center">
                                        <EyeIcon visible={confirmPasswordVisible} />
                                    </button>
                                </div>
                            </div>

                            <InputField name="address" label="Address" placeholder="Address" value={formData.address} onChange={onChange} maxLength={50} />

                            {/* State Dropdown */}
                            <div>
                                <label className="block text-sm font-medium text-blue-700">State</label>
                                <select name="state" value={formData.state} onChange={onChange} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500">
                                    <option value="">Select a state</option>
                                    {states.map(s => <option key={s} value={s}>{s}</option>)}
                                </select>
                            </div>

                            {/* City Dropdown (dynamically populated) */}
                            <div>
                                <label className="block text-sm font-medium text-blue-700">City</label>
                                <select name="city" value={formData.city} onChange={onChange} required disabled={!formData.state} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm disabled:bg-gray-100 focus:outline-none focus:ring-blue-500 focus:border-blue-500">
                                    <option value="">Select a city</option>
                                    {cities.map(c => <option key={c} value={c}>{c}</option>)}
                                </select>
                            </div>

                            <InputField name="zipcode" label="Zipcode" placeholder="zipcode" value={formData.zipcode} onChange={onChange} />
                        </div>

                        <div className="flex items-center">
                            <input id="agreeTerms" name="agreeTerms" type="checkbox" checked={agreeTerms} onChange={(e) => setAgreeTerms(e.target.checked)} className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"/>
                            <label htmlFor="agreeTerms" className="ml-2 block text-sm text-gray-900">I agree to Your <a href="#" className="font-medium text-blue-600 hover:text-blue-500">Terms and conditions</a></label>
                        </div>

                        {error && <div className="text-center p-2 bg-red-100 text-red-700 rounded-md">{error}</div>}

                        <div>
                            <button type="submit" disabled={loading} className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:bg-blue-300">
                                {loading ? 'Creating Account...' : 'Create Account'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default RegisterPage;
