import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
    return (
        <div className="bg-white rounded shadow-md overflow-hidden flex flex-col justify-between transition-transform duration-200 hover:-translate-y-1">
            <Link to={`/product/${product._id}`}>
                <img
                    src={product.imageUrl}
                    alt={product.name}
                    className="w-full h-52 object-cover"
                />
            </Link>
            <div className="p-4">
                <Link to={`/product/${product._id}`}>
                    <h2 className="text-xl font-semibold mb-2">{product.name}</h2>
                </Link>
                <h3 className="text-gray-600 text-lg mb-3">${product.price.toFixed(2)}</h3>
                <p className="text-sm text-gray-700 mb-4">
                    {product.description.substring(0, 60)}...
                </p>
                <button className="bg-gray-800 text-white py-2 px-4 rounded w-full hover:bg-gray-600 transition-colors">
                    Add to Cart
                </button>
            </div>
        </div>
    );
};

export default ProductCard;
