import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const AdminRoute = () => {
    const { isAuthenticated, isAdmin, user } = useAuth();

    // This check is simple. In a real app, you might want a loading state
    // while the user state is being determined.
    if (!isAuthenticated) {
        // If not logged in, redirect to login
        return <Navigate to="/login" />;
    }

    if (!isAdmin) {
        // If logged in but not an admin, show an unauthorized message or page
        return (
            <div className="text-center mt-20">
                <h1 className="text-3xl font-bold text-red-600">Access Denied</h1>
                <p className="mt-4">You do not have permission to view this page.</p>
            </div>
        );
    }

    // If logged in and is an admin, show the nested routes/component
    return <Outlet />;
};

export default AdminRoute;
