import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import productService from '../services/productService';

const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);
    const [currentVariant, setCurrentVariant] = useState(null);

    // --- Fetch Product Data ---
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    setProduct(res.data);
                    if (res.data.productType === 'variant' && res.data.variants.length > 0) {
                        const initialOptions = {};
                        res.data.optionNames.forEach(name => {
                            initialOptions[name] = res.data.variants[0].options.find(opt => opt.name === name)?.value;
                        });
                        setSelectedOptions(initialOptions);
                    }
                } else {
                    setError('Product not found.');
                }
            } catch (err) {
                setError('Failed to fetch product data.');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    // --- Find Current Variant ---
    useEffect(() => {
        if (product?.productType === 'variant') {
            const matchedVariant = product.variants.find(variant =>
                Object.entries(selectedOptions).every(
                    ([name, value]) => variant.options.some(opt => opt.name === name && opt.value === value)
                )
            );
            setCurrentVariant(matchedVariant || null);
            setQuantity(1);
        }
    }, [selectedOptions, product]);

    // --- Memoize Available Options ---
    const availableOptions = useMemo(() => {
        if (!product || product.productType !== 'variant') return {};
        const options = {};
        product.optionNames.forEach(name => {
            options[name] = [...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))];
        });
        return options;
    }, [product]);

    // --- Handlers ---
    const handleOptionSelect = (name, value) => {
        setSelectedOptions(prev => ({ ...prev, [name]: value }));
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQty = prev + amount;
            const maxStock = product.productType === 'simple'
                ? product.stock
                : currentVariant?.stock || 0;
            if (newQty < 1) return 1;
            if (newQty > maxStock) return maxStock;
            return newQty;
        });
    };

    const isOptionAvailable = (optionName, value) => {
        if (!product || product.productType === 'simple') return true;
        return true; // Smart availability logic could go here
    };

    if (loading) return <div className="text-center mt-20">Loading...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                {/* Left: Image */}
                <div>
                    <img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" />
                </div>

                {/* Right: Info */}
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    {/* --- Price --- */}
                    <div className="text-4xl font-extrabold text-gray-900 mb-6">
                        {product.productType === 'simple'
                            ? `₹${product.price.toFixed(2)}`
                            : currentVariant
                                ? `₹${currentVariant.price.toFixed(2)}`
                                : 'Select options to see price'}
                    </div>

                    {/* --- Variant Selectors --- */}
                    {product.productType === 'variant' && (
                        <div className="space-y-6">
                            {product.optionNames.map(name => (
                                <div key={name}>
                                    <h3 className="text-lg font-semibold mb-2">
                                        {name}: <span className="font-normal">{selectedOptions[name]}</span>
                                    </h3>
                                    <div className="flex flex-wrap gap-3">
                                        {availableOptions[name].map(value => {
                                            const isSelected = selectedOptions[name] === value;
                                            const isAvailable = isOptionAvailable(name, value);
                                            return (
                                                <button
                                                    key={value}
                                                    onClick={() => handleOptionSelect(name, value)}
                                                    disabled={!isAvailable}
                                                    className={`px-6 py-2 border rounded-md transition-all font-medium ${
                                                        isSelected
                                                            ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500'
                                                            : 'border-gray-300 hover:border-gray-500'
                                                    } ${!isAvailable && 'opacity-50 cursor-not-allowed'}`}
                                                >
                                                    {value}
                                                </button>
                                            );
                                        })}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* --- Stock & Quantity --- */}
                    <div className="mt-8">
                        <p className={`text-lg font-semibold ${(
                            product.productType === 'simple'
                                ? product.stock
                                : currentVariant?.stock
                        ) > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {product.productType === 'simple'
                                ? `${product.stock} in stock`
                                : currentVariant
                                    ? `${currentVariant.stock} in stock`
                                    : 'This combination is not available.'}
                        </p>

                        {((product.productType === 'simple' && product.stock > 0) ||
                            (product.productType === 'variant' && currentVariant?.stock > 0)) && (
                            <div className="flex items-center mt-4">
                                <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                                <div className="flex items-center border border-gray-300 rounded-md">
                                    <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                    <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                    <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* --- Add to Cart --- */}
                    <div className="mt-8">
                        <button
                            disabled={
                                (product.productType === 'simple' && product.stock === 0) ||
                                (product.productType === 'variant' && (!currentVariant || currentVariant.stock === 0))
                            }
                            className="w-full py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
                        >
                            {(product.productType === 'simple' && product.stock > 0) ||
                            (product.productType === 'variant' && currentVariant?.stock > 0)
                                ? 'Add to Cart'
                                : 'Out of Stock'}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
