import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import authService from '../services/authService';
import GooglePlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-google-places-autocomplete';

// Eye icon for password visibility toggle (no changes needed here)
const EyeIcon = ({ onClick, isVisible }) => (
    <svg onClick={onClick} xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-400 cursor-pointer" viewBox="0 0 20 20" fill="currentColor">
        {isVisible ? (
            <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
        ) : (
            <path fillRule="evenodd" d="M10 3a7 7 0 00-7 7c0 1.545.446 2.973 1.215 4.195.16.2.33.394.508.58.178.186.364.364.552.53C6.46 16.49 8.12 17 10 17s3.54-.51 4.725-1.695c.188-.166.374-.344.552-.53.178-.186.348-.38.508-.58A6.97 6.97 0 0017 10a7 7 0 00-7-7zm-5.707 7.293a5.002 5.002 0 018.829-1.393A4.997 4.997 0 0110 15a4.997 4.997 0 01-4.33-2.5A4.985 4.985 0 014.293 10.293z" clipRule="evenodd" />
        )}
    </svg>
);


const RegisterPage = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '', lastName: '', email: '', phone: '',
        password: '', confirmPassword: '',
        address: '', city: '', state: '', zipcode: '',
    });
    const [addressValue, setAddressValue] = useState(null);
    const [agreed, setAgreed] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    // NEW STATE: This will track if we should disable the manual fields.
    const [isAddressFromApi, setIsAddressFromApi] = useState(false);

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    // UPDATED: This function now handles both selecting and clearing the address
    const handleAddressSelect = async (place) => {
        // If the place is null, it means the user cleared the input.
        if (!place) {
            setAddressValue(null);
            setFormData(prev => ({ ...prev, address: '', city: '', state: '', zipcode: '' }));
            setIsAddressFromApi(false); // Unlock manual fields
            return;
        }

        setAddressValue(place);
        try {
            const results = await geocodeByAddress(place.label);
            const latLng = await getLatLng(results[0]);

            let city = '', state = '', zipcode = '';
            const addressComponents = results[0].address_components;

            for (const component of addressComponents) {
                const types = component.types;
                if (types.includes('locality')) city = component.long_name;
                if (types.includes('administrative_area_level_1')) state = component.long_name;
                if (types.includes('postal_code')) zipcode = component.long_name;
            }

            setFormData(prev => ({
                ...prev,
                address: place.label, // Use the full label from Google
                city,
                state,
                zipcode
            }));
            setIsAddressFromApi(true); // Data is from API, so lock manual fields
        } catch (error) {
            console.error("Geocoding Error:", error);
            // If geocoding fails, ensure we fallback to manual mode
            setIsAddressFromApi(false);
        }
    };

    const onSubmit = async e => {
        e.preventDefault();
        setError('');
        if (formData.password !== formData.confirmPassword) return setError('Passwords do not match.');
        if (formData.password.length < 8) return setError('Password must be at least 8 characters long.');
        if (!agreed) return setError('You must agree to the terms and conditions.');
        // Check for empty address fields, which are all required
        if (!formData.address || !formData.city || !formData.state || !formData.zipcode) {
            return setError('Please complete all address fields.');
        }


        setLoading(true);
        try {
            await authService.register(formData);
            alert('Registration successful! Please sign in.');
            navigate('/login');
        } catch (err) {
            setError(err.msg || 'An error occurred during registration.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-4xl w-full space-y-8 p-10 bg-white rounded-xl shadow-lg">
                <div>
                    <h2 className="mt-6 text-3xl font-extrabold text-gray-900" style={{ color: '#0d6efd' }}>Sign up</h2>
                    <p className="mt-2 text-sm text-gray-600">No Account? Register control your order.</p>
                </div>
                <form className="mt-8 space-y-6" onSubmit={onSubmit}>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-x-6 gap-y-4">
                        {/* Input Fields (no changes needed here) */}
                        <InputField label="First name" name="firstName" value={formData.firstName} onChange={onChange} placeholder="first name" />
                        <InputField label="Last name" name="lastName" value={formData.lastName} onChange={onChange} placeholder="last name" />
                        <InputField label="Email address" name="email" type="email" value={formData.email} onChange={onChange} placeholder="email address" />
                        <InputField label="Phone number" name="phone" value={formData.phone} onChange={onChange} placeholder="mobile number" />
                        <PasswordField label="Password" name="password" value={formData.password} onChange={onChange} show={showPassword} toggle={() => setShowPassword(!showPassword)} />
                        <PasswordField label="Confirm password" name="confirmPassword" value={formData.confirmPassword} onChange={onChange} show={showConfirmPassword} toggle={() => setShowConfirmPassword(!showConfirmPassword)} />

                        {/* UPDATED Google Places Autocomplete to also handle manual input */}
                        <div className="md:col-span-2">
                            <label className="text-sm font-medium" style={{ color: '#0d6efd' }}>Address</label>
                            <GooglePlacesAutocomplete
                                apiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
                                selectProps={{
                                    value: addressValue,
                                    onChange: handleAddressSelect, // Handles selection AND clearing
                                    placeholder: 'Start typing your address...',
                                    // This allows free-form typing to be captured if user doesn't select an option
                                    onInputChange: (text) => {
                                        if (!isAddressFromApi) { // Only update if not locked by API
                                            setFormData(prev => ({ ...prev, address: text }));
                                        }
                                    },
                                    styles: {
                                        input: (provided) => ({ ...provided, height: '42px', borderRadius: '0.375rem' }),
                                        control: (provided) => ({ ...provided, borderRadius: '0.375rem', boxShadow: 'none', '&:hover': {borderColor: '#a3a3a3'} })
                                    }
                                }}
                            />
                        </div>

                        {/* UPDATED manual fields to be conditionally disabled */}
                        <InputField label="City" name="city" value={formData.city} onChange={onChange} placeholder="city" disabled={isAddressFromApi} />
                        <InputField label="State" name="state" value={formData.state} onChange={onChange} placeholder="state" disabled={isAddressFromApi} />
                        <InputField label="Zipcode" name="zipcode" value={formData.zipcode} onChange={onChange} placeholder="zipcode" disabled={isAddressFromApi} />
                    </div>

                    <div className="flex items-center">
                        <input id="terms" name="terms" type="checkbox" checked={agreed} onChange={(e) => setAgreed(e.target.checked)} className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded" />
                        <label htmlFor="terms" className="ml-2 block text-sm text-gray-900">I agree to Your <a href="#" className="font-medium" style={{ color: '#0d6efd' }}>Terms and conditions</a></label>
                    </div>

                    {error && <p className="text-sm text-red-600 text-center">{error}</p>}

                    <div>
                        <button type="submit" disabled={loading} className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400">
                            {loading ? 'Registering...' : 'Sign up'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

// Helper components for inputs to keep the form clean (no changes needed here)
const InputField = ({ label, name, type = 'text', value, onChange, placeholder, disabled = false }) => (
    <div>
        <label htmlFor={name} className="text-sm font-medium" style={{ color: '#0d6efd' }}>{label}</label>
        <input id={name} name={name} type={type} required value={value} onChange={onChange} placeholder={placeholder} disabled={disabled}
               className={`mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm ${disabled ? 'bg-gray-100 cursor-not-allowed' : 'bg-white'}`} />
    </div>
);

const PasswordField = ({ label, name, value, onChange, show, toggle }) => (
    <div>
        <label htmlFor={name} className="text-sm font-medium" style={{ color: '#0d6efd' }}>{label}</label>
        <div className="relative">
            <input id={name} name={name} type={show ? 'text' : 'password'} required value={value} onChange={onChange} placeholder="Minimum 8 characters long"
                   className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
            <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                <EyeIcon onClick={toggle} isVisible={show} />
            </div>
        </div>
    </div>
);

export default RegisterPage;
