import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

// Helper component to render the category tree recursively
const CategoryTree = ({ categories, onDelete }) => {
    if (!categories || categories.length === 0) return null;
    return (
        <ul className="space-y-2">
            {categories.map(cat => (
                <li key={cat._id} className="border rounded-md p-3 bg-gray-50">
                    <div className="flex justify-between items-center">
                        <span className="font-semibold">{cat.name}</span>
                        <div className="space-x-3">
                            <Link to={`/admin/categories/edit/${cat._id}`} className="text-sm text-blue-600 hover:text-blue-800">Edit</Link>
                            <button onClick={() => onDelete(cat._id)} className="text-sm text-red-600 hover:text-red-800">Delete</button>
                        </div>
                    </div>
                    {/* Recursive call for subcategories */}
                    {cat.children && cat.children.length > 0 && (
                        <div className="mt-2 pl-6 border-l-2">
                            <CategoryTree categories={cat.children} onDelete={onDelete} />
                        </div>
                    )}
                </li>
            ))}
        </ul>
    );
};


const CategoryListPage = () => {
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);

    const fetchCategories = async () => {
        setLoading(true);
        try {
            const token = JSON.parse(localStorage.getItem('userToken'));
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const res = await axios.get('http://localhost:5000/api/categories', config);
            if (res.data.success) {
                setCategories(res.data.data);
            }
        } catch (error) { console.error("Fetch error", error); }
        finally { setLoading(false); }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure? This will also delete all subcategories.')) {
            try {
                const token = JSON.parse(localStorage.getItem('userToken'));
                const config = { headers: { Authorization: `Bearer ${token}` } };
                await axios.delete(`http://localhost:5000/api/categories/${id}`, config);
                fetchCategories(); // Refresh the list
            } catch (error) {
                console.error("Delete error", error);
            }
        }
    };

    if (loading) return <p>Loading categories...</p>;

    return (
        <div className="container mx-auto p-4">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-2xl font-bold">Manage Categories</h1>
                <Link to="/admin/categories/add" className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600">
                    Add New Category
                </Link>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md">
                <CategoryTree categories={categories} onDelete={handleDelete} />
            </div>
        </div>
    );
};

export default CategoryListPage;
