import React, { useState, useEffect } from 'react';
import axios from 'axios';
import categoryService from '../../services/categoryService';

// Reusable Modal Component for the form
const CategoryModal = ({ isOpen, onClose, onSubmit, category, allCategories }) => {
    if (!isOpen) return null;

    const [name, setName] = useState(category?.name || '');
    const [parent, setParent] = useState(category?.parent || '');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit({ ...category, name, parent });
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md">
                <h2 className="text-xl font-bold mb-4">{category?._id ? 'Edit' : 'Create'} Category</h2>
                <form onSubmit={handleSubmit}>
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium">Name</label>
                            <input type="text" value={name} onChange={(e) => setName(e.target.value)} required className="mt-1 w-full p-2 border rounded-md" />
                        </div>
                        <div>
                            <label className="block text-sm font-medium">Parent Category</label>
                            <select value={parent} onChange={(e) => setParent(e.target.value)} className="mt-1 w-full p-2 border rounded-md">
                                <option value="">None (Top-Level Category)</option>
                                {allCategories.map(cat => (
                                    <option key={cat._id} value={cat._id} disabled={cat._id === category?._id}>
                                        {cat.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </div>
                    <div className="mt-6 flex justify-end gap-4">
                        <button type="button" onClick={onClose} className="px-4 py-2 bg-gray-300 rounded-md">Cancel</button>
                        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded-md">Save</button>
                    </div>
                </form>
            </div>
        </div>
    );
};


const CategoryManagementPage = () => {
    const [categories, setCategories] = useState([]);
    const [flatCategories, setFlatCategories] = useState([]); // For the dropdown
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // State for the modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentCategory, setCurrentCategory] = useState(null);

    const fetchCategories = async () => {
        setLoading(true);
        try {
            const res = await axios.get('/api/categories');

            setCategories(res.data.data);

            const flatList = [];
            const flatten = (cat, level = 0) => {
                flatList.push({_id: cat._id, name: `${'—'.repeat(level)} ${cat.name}`});
                if (cat.children) cat.children.forEach(child => flatten(child, level + 1));
            };
            res.data.data.forEach(cat => flatten(cat));
            setFlatCategories(flatList);

        } catch (err) { setError('Failed to fetch categories.'); }
        finally { setLoading(false); }
    };

    useEffect(() => { fetchCategories(); }, []);

    const handleOpenModal = (category = null) => {
        setCurrentCategory(category);
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setCurrentCategory(null);
    };

    const handleFormSubmit = async (categoryData) => {
        try {
            if (categoryData._id) {
                // Update
                await categoryService.updateCategory(categoryData._id, categoryData);
            } else {
                // Create
                await categoryService.createCategory(categoryData);
            }
            fetchCategories(); // Refresh list
        } catch (err) {
            setError(err.response?.data?.msg || 'Operation failed.');
        } finally {
            handleCloseModal();
        }
    };

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure? This cannot be undone.')) {
            try {
                await categoryService.deleteCategory(id);
                fetchCategories();
            } catch (err) {
                setError(err.response?.data?.msg || 'Failed to delete.');
            }
        }
    };

    const renderCategoryList = (cats) => (
        cats.map(cat => (
            <React.Fragment key={cat._id}>
                <tr className="hover:bg-gray-50">
                    <td className="px-6 py-4">{cat.name}</td>
                    <td className="px-6 py-4 text-sm font-medium text-right">
                        <button onClick={() => handleOpenModal(cat)} className="text-indigo-600 hover:text-indigo-900 mr-4">Edit</button>
                        <button onClick={() => handleDelete(cat._id)} className="text-red-600 hover:text-red-900">Delete</button>
                    </td>
                </tr>
                {cat.children && <tr><td colSpan="2" className="p-0"><table className="min-w-full"><tbody className="pl-4">{renderCategoryList(cat.children)}</tbody></table></td></tr>}
            </React.Fragment>
        ))
    );

    return (
        <div className="container mx-auto p-4">
            <CategoryModal isOpen={isModalOpen} onClose={handleCloseModal} onSubmit={handleFormSubmit} category={currentCategory} allCategories={flatCategories} />
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-2xl font-bold">Manage Categories</h1>
                <button onClick={() => handleOpenModal()} className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600">
                    Add New Category
                </button>
            </div>
            {error && <p className="text-red-500 bg-red-100 p-2 rounded-md mb-4">{error}</p>}
            <div className="bg-white shadow-md rounded-lg overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50"><tr><th className="px-6 py-3 text-left font-medium text-gray-500 uppercase">Name</th><th className="relative px-6 py-3"><span className="sr-only">Actions</span></th></tr></thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                    {loading ? <tr><td colSpan="2" className="text-center py-4">Loading...</td></tr> : renderCategoryList(categories)}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default CategoryManagementPage;
