import React, { useState, useEffect } from 'react';
import productService from '../services/productService';
import ProductCard from '../components/products/ProductCard';

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                const fetchedData = await productService.getProducts();

                // --- FIX ---
                // The API likely returns an object like { products: [...] }.
                // We need to set the state with the array inside that object.
                // The `|| []` ensures that if `fetchedData.products` is not available,
                // we fall back to an empty array, preventing another crash.
                setProducts(fetchedData.products || []);

                setError(null);
            } catch (err) {
                setError('Failed to fetch products. Please try again later.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

    if (loading) {
        return <div className="text-center mt-20 text-xl">Loading...</div>;
    }

    if (error) {
        return <div className="text-center mt-20 text-xl text-red-500">{error}</div>;
    }

    // Add a check here for safety, in case the API returns an empty list
    if (!products || products.length === 0) {
        return <div className="text-center mt-20 text-xl">No products found.</div>;
    }

    return (
        <div className="bg-gray-50 min-h-screen">
            <div className="container mx-auto px-6 py-8">
                <h1 className="text-4xl font-extrabold text-gray-800 text-center mb-12">Featured Products</h1>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                    {products.map((product) => (
                        <ProductCard key={product._id} product={product} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
