import React from 'react';
import { Routes, Route } from 'react-router-dom';

// Import Pages
import HomePage from '../pages/HomePage';
import RegisterPage from '../pages/RegisterPage';
import LoginPage from '../pages/LoginPage';

// Import the NEW Admin Layout and pages
import AdminLayout from './AdminLayout';
import AdminDashboardPage from '../pages/admin/AdminDashboardPage';
import ProductListPage from '../pages/admin/products/ProductListPage';
import AddProductPage from '../pages/admin/products/AddProductPage';
import EditProductPage from '../pages/admin/products/EditProductPage';

const AppRoutes = () => {
    return (
        <div>
            <Routes>
                {/* === Public User Routes === */}
                <Route path="/" element={<HomePage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/login" element={<LoginPage />} />

                {/* === Admin Gateway and Protected Routes === */}
                {/* The AdminLayout component now acts as the guard */}
                <Route path="/admin" element={<AdminLayout />}>
                    {/* If login is successful, Outlet will render these */}
                    <Route index element={<AdminDashboardPage />} />
                    <Route path="products" element={<ProductListPage />} />
                    <Route path="products/add" element={<AddProductPage />} />
                    <Route path="products/edit/:id" element={<EditProductPage />} />
                </Route>

            </Routes>
        </div>
    );
};

export default AppRoutes;
