import React, {useState, useEffect} from 'react';
import productService from '../../../services/productService';

const CategorySidebar = ({onFilterChange, activeFilters}) => {
    const [categories, setCategories] = useState([]);
    const [expandedCategory, setExpandedCategory] = useState(null);

    useEffect(() => {
        const fetchCategories = async () => {
            const result = await productService.getCategories();
            if (result.success) {
                setCategories(result.data);
            }
        };
        fetchCategories();
    }, []);

    const handleCategoryClick = (categoryId) => {
        onFilterChange({category: categoryId, subcategory: ''});
        setExpandedCategory(categoryId === expandedCategory ? null : categoryId);
    };

    const handleSubcategoryClick = (categoryId, subcategoryName) => {
        onFilterChange({category: categoryId, subcategory: subcategoryName});
    };

    return (
        <aside className="w-full md:w-64 lg:w-72 bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Categories</h3>
            <button
                onClick={() => onFilterChange({category: '', subcategory: ''})}
                className="w-full text-left text-purple-600 font-semibold mb-4 hover:text-purple-800"
            >
                All Products
            </button>
            <ul className="space-y-2">
                {categories.map((cat) => (
                    <li key={cat._id}>
                        <button
                            onClick={() => handleCategoryClick(cat._id)}
                            className={`w-full text-left font-semibold ${activeFilters.category === cat._id ? 'text-indigo-600' : 'text-gray-700'}`}
                        >
                            {cat.name}
                        </button>
                        {expandedCategory === cat._id && (
                            <ul className="pl-4 mt-2 space-y-1 border-l-2 border-gray-200">
                                {cat.subcategories.map((sub) => (
                                    <li key={sub}>
                                        <button
                                            onClick={() => handleSubcategoryClick(cat._id, sub)}
                                            className={`w-full text-left text-sm ${activeFilters.subcategory === sub ? 'text-indigo-600 font-bold' : 'text-gray-600 hover:text-indigo-600'}`}
                                        >
                                            {sub}
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </li>
                ))}
            </ul>
        </aside>
    );
};

export default CategorySidebar;
