import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const CategoryListPage = () => {
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const fetchCategories = async () => {
        try {
            const res = await axios.get('http://localhost:5000/api/categories');
            if (res.data.success) {
                setCategories(res.data.data);
            }
        } catch (err) {
            setError('Failed to fetch categories.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this category?')) {
            try {
                const token = JSON.parse(localStorage.getItem('userToken'));
                const config = { headers: { Authorization: `Bearer ${token}` } };
                await axios.delete(`http://localhost:5000/api/categories/${id}`, config);
                fetchCategories(); // Refresh list after delete
            } catch (err) {
                alert(err.response?.data?.msg || 'Failed to delete category.');
            }
        }
    };

    const renderCategories = (categoryList, level = 0) => {
        return categoryList.map(category => (
            <React.Fragment key={category._id}>
                <tr className="bg-white border-b">
                    <td className="py-4 px-6" style={{ paddingLeft: `${1.5 + level * 2}rem` }}>{category.name}</td>
                    <td className="py-4 px-6">{category.slug}</td>
                    <td className="py-4 px-6 text-sm font-medium">
                        <Link to={`/admin/categories/edit/${category._id}`} className="text-indigo-600 hover:text-indigo-900 mr-4">Edit</Link>
                        <button onClick={() => handleDelete(category._id)} className="text-red-600 hover:text-red-900">Delete</button>
                    </td>
                </tr>
                {category.children && category.children.length > 0 && renderCategories(category.children, level + 1)}
            </React.Fragment>
        ));
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p className="text-red-500">{error}</p>;

    return (
        <div>
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-2xl font-bold">Manage Categories</h1>
                <Link to="/admin/categories/add" className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600">
                    Add New Category
                </Link>
            </div>
            <div className="overflow-x-auto relative shadow-md sm:rounded-lg">
                <table className="w-full text-sm text-left text-gray-500">
                    <thead className="text-xs text-gray-700 uppercase bg-gray-50">
                    <tr>
                        <th scope="col" className="py-3 px-6">Name</th>
                        <th scope="col" className="py-3 px-6">Slug</th>
                        <th scope="col" className="py-3 px-6">Actions</th>
                    </tr>
                    </thead>
                    <tbody>{renderCategories(categories)}</tbody>
                </table>
            </div>
        </div>
    );
};

export default CategoryListPage;
