import React from 'react';
import { Link } from 'react-router-dom';

// This is the Cart icon from a previous step, it's good to keep it.
const CartPlusIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
        <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
        <path fillRule="evenodd" d="M10 5a1 1 0 011 1v3h3a1 1 0 110 2h-3v3a1 1 0 11-2 0v-3H6a1 1 0 110-2h3V6a1 1 0 011-1z" clipRule="evenodd" />
    </svg>
);

const ProductCard = ({ product }) => {
    // --- THIS IS THE FIX ---

    // We need to safely get the price from the first variant.
    // First, check if variants exist and if the array is not empty.
    const hasVariants = product.variants && product.variants.length > 0;
    // Get the price from the first variant, or set a default if none exist.
    const displayPrice = hasVariants ? product.variants[0].price : product.variants[1].price;

    return (
        <div className="bg-white rounded-lg border border-gray-200 shadow-sm overflow-hidden group flex flex-col">
            {/* The link should go to a future product detail page */}
            <Link to={`/product/${product._id}`} className="block overflow-hidden">
                <img
                    src={product.imageUrl}
                    alt={product.name}
                    className="w-full h-56 object-cover group-hover:scale-105 transition-transform duration-300"
                />
            </Link>

            <div className="p-4 flex flex-col flex-grow">
                <h2 className="text-lg font-semibold text-gray-800 truncate mb-1" title={product.name}>
                    <Link to={`/product/${product._id}`} className="hover:text-indigo-600">{product.name}</Link>
                </h2>

                <p className="text-sm text-gray-500 mb-4 flex-grow">
                    {product.description.substring(0, 70)}{product.description.length > 70 && '...'}
                </p>

                <div className="flex items-center justify-between mt-auto">
                    {/* Display the price safely */}
                    <span className="text-2xl font-bold text-gray-900">
                        {hasVariants ? 'From ' : ''}â‚¹{displayPrice.toFixed(2)}
                    </span>

                    {/*
                      The "Add to Cart" button is no longer logical here because the user must select a variant first.
                      We will change this to a "View Options" link that goes to the product detail page.
                    */}
                    <Link
                        to={`/product/${product._id}`}
                        className="flex items-center justify-center px-4 py-2 bg-indigo-600 text-white text-sm font-semibold rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 transition-colors"
                    >
                        <CartPlusIcon />
                        Select Options
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default ProductCard;
