import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import productService from '../services/productService';

// This is the component that will now work correctly.
const ProductDetailPage = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // This state holds the user's current selection, e.g., { Color: 'Red', Size: 'M' }
    const [selectedOptions, setSelectedOptions] = useState({});
    const [quantity, setQuantity] = useState(1);

    // 1. --- Fetch Product Data ---
    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            try {
                const res = await productService.getProductById(id);
                if (res.success) {
                    const fetchedProduct = res.data;
                    setProduct(fetchedProduct);

                    // --- ROBUST INITIALIZATION ---
                    // If it's a variant product with variants, pre-select the first one.
                    if (fetchedProduct.productType === 'variant' && fetchedProduct.variants?.length > 0) {
                        const firstVariantOptions = {};
                        // Create the selectedOptions object directly from the first variant's options.
                        // This GUARANTEES the initial state is a valid, existing variant.
                        fetchedProduct.variants[0].options.forEach(opt => {
                            firstVariantOptions[opt.name] = opt.value;
                        });
                        setSelectedOptions(firstVariantOptions);
                    }
                } else { setError('Product not found.'); }
            } catch (err) { setError('Failed to fetch product data.'); }
            finally { setLoading(false); }
        };
        fetchProduct();
    }, [id]);

    // 2. --- THE CORRECTED "BRAIN" ---
    // This hook correctly finds the variant that matches the user's selection.
    const currentVariant = useMemo(() => {
        // Don't do anything if it's not a variant product or options aren't selected yet.
        if (!product || product.productType !== 'variant' || Object.keys(selectedOptions).length < product.optionNames.length) {
            return null;
        }

        // Find the variant where every option matches the selected option.
        return product.variants.find(variant => {
            return variant.options.every(option => {
                return selectedOptions[option.name] === option.value;
            });
        });

    }, [selectedOptions, product]);

    // --- Handlers remain the same, they are correct ---
    const handleOptionSelect = (optionName, value) => {
        setSelectedOptions(prev => ({ ...prev, [optionName]: value }));
        setQuantity(1);
    };

    const handleQuantityChange = (amount) => {
        setQuantity(prev => {
            const newQuantity = prev + amount;
            if (newQuantity < 1) return 1;
            const maxStock = product.productType === 'simple' ? product.stock : (currentVariant ? currentVariant.stock : 0);
            if (newQuantity > maxStock) return maxStock;
            return newQuantity;
        });
    };

    if (loading) return <div className="text-center mt-20">Loading Product Details...</div>;
    if (error) return <div className="text-center mt-20 text-red-500">{error}</div>;
    if (!product) return null;

    // --- Derived state for cleaner JSX ---
    const stock = product.productType === 'simple' ? product.stock : (currentVariant ? currentVariant.stock : 0);
    const price = product.productType === 'simple' ? product.price : (currentVariant ? currentVariant.price : 0);
    const canAddToCart = product.productType === 'simple' ? stock > 0 : (!!currentVariant && stock > 0);

    return (
        <div className="container mx-auto max-w-6xl py-12 px-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                <div><img src={product.imageUrl} alt={product.name} className="w-full rounded-lg shadow-lg" /></div>
                <div>
                    <h1 className="text-4xl font-bold mb-2">{product.name}</h1>
                    <p className="text-gray-500 mb-6">{product.category}</p>
                    <p className="text-lg text-gray-700 mb-6">{product.description}</p>

                    {/* --- Price Display --- */}
                    <div className="text-4xl font-extrabold text-gray-900 mb-6">
                        {product.productType === 'simple' || currentVariant ? `â‚¹${price.toFixed(2)}` : 'Select options to see price'}
                    </div>

                    {/* --- Variant Option Selectors --- */}
                    {product.productType === 'variant' && (
                        <div className="space-y-6">
                            {product.optionNames.map(name => (
                                <div key={name}>
                                    <h3 className="text-lg font-semibold mb-2">{name}: <span className="font-normal">{selectedOptions[name]}</span></h3>
                                    <div className="flex flex-wrap gap-3">
                                        {[...new Set(product.variants.map(v => v.options.find(opt => opt.name === name)?.value))].map(value => (
                                            <button key={value} onClick={() => handleOptionSelect(name, value)} className={`px-6 py-2 border rounded-md font-medium transition-all ${selectedOptions[name] === value ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-500' : 'border-gray-300 hover:border-gray-500'}`}>
                                                {value}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}

                    {/* --- Stock, Quantity, and Add to Cart --- */}
                    <div className="mt-8">
                        <p className={`text-lg font-semibold ${stock > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {product.productType === 'variant' && !currentVariant ? 'This specific combination is not available.' : `${stock} in stock`}
                        </p>
                        {stock > 0 && (
                            <div className="flex items-center mt-4">
                                <h3 className="text-lg font-semibold mr-4">Quantity:</h3>
                                <div className="flex items-center border border-gray-300 rounded-md">
                                    <button onClick={() => handleQuantityChange(-1)} className="px-4 py-2 font-bold text-lg">-</button>
                                    <span className="px-6 py-2 border-l border-r">{quantity}</span>
                                    <button onClick={() => handleQuantityChange(1)} className="px-4 py-2 font-bold text-lg">+</button>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="mt-8">
                        <button disabled={!canAddToCart} className="w-full py-4 text-lg font-bold text-white bg-purple-600 rounded-lg hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed">
                            {canAddToCart ? 'Add to Cart' : (product.productType === 'variant' ? 'Unavailable' : 'Out of Stock')}
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetailPage;
