import React, { useState, useEffect } from 'react';
import productService from '../services/productService';
import ProductCard from '../components/products/ProductCard';

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                setLoading(true);
                // The service now returns the { success, data } object directly
                const result = await productService.getProducts();

                // --- THIS IS THE FIX ---
                // We now correctly access the 'data' property of the result
                if (result.success) {
                    setProducts(result.data);
                } else {
                    setError('Failed to fetch products.');
                }

            } catch (err) {
                setError('Failed to fetch products. Please try again later.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchProducts();
    }, []);

    // ... the rest of the component remains the same
    if (loading) {
        return <div className="text-center py-20 text-xl font-medium text-gray-600">Loading Products...</div>;
    }

    if (error) {
        return <div className="text-center py-20 text-xl font-medium text-red-500">{error}</div>;
    }

    return (
        <div className="bg-gray-50">
            <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-12">
                <div className="text-center mb-12">
                    <h1 className="text-4xl font-extrabold text-gray-900 sm:text-5xl">
                        Featured Products
                    </h1>
                    <p className="mt-4 max-w-2xl mx-auto text-lg text-gray-500">
                        Top-quality gear and apparel for every sport. Find your perfect fit today.
                    </p>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                    {products.map((product) => (
                        <ProductCard key={product._id} product={product} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
