import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import CategoryDropdown from '../../../components/admin/CategoryDropdown';

const EditCategoryPage = () => {
    const [name, setName] = useState('');
    const [parent, setParent] = useState('');
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchCategory = async () => {
            try {
                const token = JSON.parse(localStorage.getItem('userToken'));
                const config = { headers: { Authorization: `Bearer ${token}` } };
                const { data } = await axios.get(`http://localhost:5000/api/categories/${id}`, config);
                setName(data.data.name);
                setParent(data.data.parent || '');
                setLoading(false);
            } catch (err) { alert('Failed to fetch category data.'); }
        };
        fetchCategory();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const token = JSON.parse(localStorage.getItem('userToken'));
            const config = { headers: { Authorization: `Bearer ${token}` } };
            await axios.put(`http://localhost:5000/api/categories/${id}`, { name, parent }, config);
            navigate('/admin/categories');
        } catch (err) { alert('Failed to update category.'); setLoading(false); }
    };

    if (loading) return <p>Loading...</p>;

    return (
        <div className="max-w-lg mx-auto">
            <h1 className="text-2xl font-bold mb-6">Edit Category</h1>
            <form onSubmit={handleSubmit} className="space-y-6 bg-white p-8 rounded-lg shadow-md">
                <div>
                    <label className="block text-sm font-medium text-gray-700">Category Name</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required className="mt-1 block w-full p-2 border border-gray-300 rounded-md" />
                </div>
                <CategoryDropdown value={parent} onChange={(e) => setParent(e.target.value)} />
                <button type="submit" disabled={loading} className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300">
                    {loading ? 'Saving...' : 'Save Changes'}
                </button>
            </form>
        </div>
    );
};
export default EditCategoryPage;
