import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import CategoryDropdown from '../../../components/admin/CategoryDropdown';

const CategoryFormPage = () => {
    const [name, setName] = useState('');
    const [slug, setSlug] = useState('');
    const [parent, setParent] = useState('');
    const [loading, setLoading] = useState(false);

    const { id } = useParams(); // Will be present if we are editing
    const navigate = useNavigate();
    const isEditing = Boolean(id);

    // Fetch category data if we are editing
    useEffect(() => {
        if (isEditing) {
            const fetchCategory = async () => {
                const token = JSON.parse(localStorage.getItem('userToken'));
                const config = { headers: { Authorization: `Bearer ${token}` } };
                const res = await axios.get(`http://localhost:5000/api/categories/${id}`, config);
                const { name, slug, parent } = res.data.data;
                setName(name);
                setSlug(slug);
                setParent(parent || '');
            };
            fetchCategory();
        }
    }, [id, isEditing]);

    // Auto-generate slug from name
    useEffect(() => {
        if (!isEditing) { // Only auto-generate for new categories
            setSlug(name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)/g, ''));
        }
    }, [name, isEditing]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        const categoryData = { name, slug, parent: parent || null };
        const token = JSON.parse(localStorage.getItem('userToken'));
        const config = { headers: { Authorization: `Bearer ${token}` } };

        try {
            if (isEditing) {
                await axios.put(`http://localhost:5000/api/categories/${id}`, categoryData, config);
            } else {
                await axios.post('http://localhost:5000/api/categories', categoryData, config);
            }
            navigate('/admin/categories');
        } catch (error) {
            console.error("Failed to save category", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container mx-auto p-4 max-w-lg">
            <h1 className="text-2xl font-bold mb-4">{isEditing ? 'Edit Category' : 'Add New Category'}</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md space-y-4">
                <div>
                    <label>Name</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required className="w-full mt-1 p-2 border rounded"/>
                </div>
                <div>
                    <label>Slug (URL)</label>
                    <input type="text" value={slug} onChange={(e) => setSlug(e.target.value)} required className="w-full mt-1 p-2 border rounded bg-gray-100"/>
                </div>
                {/* Use our reusable dropdown to select a parent */}
                <CategoryDropdown value={parent} onChange={(e) => setParent(e.target.value)} />

                <button type="submit" disabled={loading} className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300">
                    {loading ? 'Saving...' : 'Save Category'}
                </button>
            </form>
        </div>
    );
};

export default CategoryFormPage;
